    <!-- jQuery and Contact Form Script -->
    <script src="https://code.jquery.com/jquery-3.7.1.min.js"></script>
    <script>
        $(document).ready(function() {
            // Form validation and submission
            $('#contactForm').on('submit', function(e) {
                e.preventDefault();

                // Reset previous validation states
                $('.form-control').removeClass('is-invalid is-valid');
                $('.invalid-feedback').empty();
                $('#contactMessages').hide();
                $('#successMessage, #errorMessage').hide();

                // Show loading state
                $('#submitBtn').prop('disabled', true);
                $('#submitText').text('{{ __("Sending...") }}');
                $('#loadingSpinner').show();

                // Get form data
                var formData = new FormData(this);

                // Client-side validation
                var isValid = true;
                var errors = {};

                // Name validation
                var name = $('#name').val().trim();
                if (name.length < 2) {
                    errors.name = '{{ __("Name must be at least 2 characters.") }}';
                    isValid = false;
                }

                // Email validation
                var email = $('#email').val().trim();
                var emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                if (!emailRegex.test(email)) {
                    errors.email = '{{ __("Please enter a valid email address.") }}';
                    isValid = false;
                }

                // Subject validation
                var subject = $('#subject').val().trim();
                if (subject.length < 3) {
                    errors.subject = '{{ __("Subject must be at least 3 characters.") }}';
                    isValid = false;
                }

                // Message validation
                var details = $('#details').val().trim();
                if (details.length < 10) {
                    errors.details = '{{ __("Message must be at least 10 characters.") }}';
                    isValid = false;
                } else if (details.length > 5000) {
                    errors.details = '{{ __("Message is too long (maximum 5000 characters).") }}';
                    isValid = false;
                }

                // Phone validation (optional but if provided, should be valid)
                var phone = $('#phone').val().trim();
                if (phone && phone.length < 10) {
                    errors.phone = '{{ __("Please enter a valid phone number.") }}';
                    isValid = false;
                }

                // Display validation errors
                if (!isValid) {
                    Object.keys(errors).forEach(function(field) {
                        $('#' + field).addClass('is-invalid');
                        $('#' + field).siblings('.invalid-feedback').text(errors[field]);
                    });

                    resetSubmitButton();
                    return;
                }

                // Add valid classes to all fields
                $('.form-control').addClass('is-valid');

                // Submit form via AJAX
                $.ajax({
                    url: $(this).attr('action'),
                    type: 'POST',
                    data: formData,
                    processData: false,
                    contentType: false,
                    headers: {
                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                    },
                    success: function(response) {
                        if (response.success) {
                            // Show success message
                            $('#successText').text(response.message);
                            $('#successMessage').show();
                            $('#contactMessages').show();

                            // Reset form
                            $('#contactForm')[0].reset();
                            $('.form-control').removeClass('is-valid is-invalid');

                            // Scroll to success message
                            $('html, body').animate({
                                scrollTop: $('#contactMessages').offset().top - 100
                            }, 500);
                        } else {
                            showError(response.message || '{{ __("An error occurred. Please try again.") }}');
                        }
                    },
                    error: function(xhr) {
                        var errorMessage = '{{ __("Sorry, there was an error sending your message. Please try again later.") }}';

                        if (xhr.status === 422) {
                            // Validation errors
                            var errors = xhr.responseJSON.errors;
                            Object.keys(errors).forEach(function(field) {
                                $('#' + field).addClass('is-invalid');
                                $('#' + field).siblings('.invalid-feedback').text(errors[field][0]);
                            });
                        } else if (xhr.responseJSON && xhr.responseJSON.message) {
                            errorMessage = xhr.responseJSON.message;
                        }

                        showError(errorMessage);
                    },
                    complete: function() {
                        resetSubmitButton();
                    }
                });
            });

            // Function to show error message
            function showError(message) {
                $('#errorText').text(message);
                $('#errorMessage').show();
                $('#contactMessages').show();

                // Scroll to error message
                $('html, body').animate({
                    scrollTop: $('#contactMessages').offset().top - 100
                }, 500);
            }

            // Function to reset submit button
            function resetSubmitButton() {
                $('#submitBtn').prop('disabled', false);
                $('#submitText').text('{{ __("Submit") }}');
                $('#loadingSpinner').hide();
            }

            // Real-time validation feedback
            $('#name, #email, #subject, #details').on('blur', function() {
                var field = $(this);
                var value = field.val().trim();
                var fieldName = field.attr('name');

                field.removeClass('is-invalid is-valid');
                field.siblings('.invalid-feedback').empty();

                if (value) {
                    var isFieldValid = true;
                    var errorMessage = '';

                    switch(fieldName) {
                        case 'name':
                            if (value.length < 2) {
                                isFieldValid = false;
                                errorMessage = '{{ __("Name must be at least 2 characters.") }}';
                            }
                            break;
                        case 'email':
                            var emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                            if (!emailRegex.test(value)) {
                                isFieldValid = false;
                                errorMessage = '{{ __("Please enter a valid email address.") }}';
                            }
                            break;
                        case 'subject':
                            if (value.length < 3) {
                                isFieldValid = false;
                                errorMessage = '{{ __("Subject must be at least 3 characters.") }}';
                            }
                            break;
                        case 'details':
                            if (value.length < 10) {
                                isFieldValid = false;
                                errorMessage = '{{ __("Message must be at least 10 characters.") }}';
                            } else if (value.length > 5000) {
                                isFieldValid = false;
                                errorMessage = '{{ __("Message is too long (maximum 5000 characters).") }}';
                            }
                            break;
                    }

                    if (isFieldValid) {
                        field.addClass('is-valid');
                    } else {
                        field.addClass('is-invalid');
                        field.siblings('.invalid-feedback').text(errorMessage);
                    }
                }
            });

            // Character counter for message field
            $('#details').on('input', function() {
                var length = $(this).val().length;
                var maxLength = 5000;
                var remaining = maxLength - length;

                // Remove existing counter
                $('.char-counter').remove();

                // Add character counter
                var counterClass = remaining < 100 ? 'text-warning' : remaining < 0 ? 'text-danger' : 'text-muted';
                $(this).after('<small class="char-counter ' + counterClass + '">' + length + '/' + maxLength + ' {{ __("characters") }}</small>');
            });
        });
    </script>
